import './App.css';
import { Bar } from 'react-chartjs-2';
import dados from '../src/dados/dadosUsina.json';
let opcao;
function App() {

  return (
    <div className="App">
      <header className="appBar">
        <ul>
          <li><a href="#">Gráficos</a></li>
          <li><a href="#">Clientes</a></li>
          <li><a href="#">Lucros</a></li>
        </ul>
      </header>
      <main className="main">
        <div className="opcaoUm">
          <div className="grafico">
            <Bar
              data={{
                labels: dados.map((elem) => ([elem.opcao])),
                datasets: [
                  {
                    label: 'temperatura por hora ',
                    data: dados.map((elem) => ([elem.tempo_h])),
                    backgroundColor: 'blue',
                  },
                ]
              }}
              height={400}
              width={600}
              options={{
                maintainAspectRatio: false,
                scales: {
                  yAxes: [
                    {
                      ticks: {
                        beginAtZero: true,
                      }
                    }
                  ]
                }
              }}
            />

          </div>
          <form>
            <p>Escolha uma opção para aparecer no gráfico</p>
            <div>
              <input type="radio" id="tensao" name="op" value="tensao_V" />
              <label for="tensao">Tensão</label>
              <input type="radio" id="temperatura" name="op" value="temperatura_C" />
              <label for="temperatura">Temperatura</label>
              <input type="radio" id="potencia" name="op" value="potencia_KW" />
              <label for="potencia">Potencia</label>
              <input type="radio" id="corrent" name="op" value="corrent_A" checked />
              <label for="corrent">Corrente</label>
            </div>
          </form>
        </div>
        <div className="o">

        </div>
      </main>
    </div>
  );

}

export default App;
