import '../design/lista.css';
import React from "react";
import useSWR from "swr";
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';


const fetcher = async (url) => {
   const res = await fetch(url);
   const json = await res.json();
   return json;
}

const Home = () => {
   const { data, mutate } = useSWR("http://localhost:3001/dados", fetcher)
   if (!data) {
      return <p>Loading...</p>
   }
   return (<>{data.map((items) => (
      <div className="ficha">
         <p className="p">Nome do Cliente: </p>
         <p className="p">{items.userclientes}</p>
         <p className="p">Identificação do Cliente: </p>
         <p className="p">{items.usinaid}</p>
         <p className="p">Participação na Usina: </p>
         <p className="p">{items.perctparticipacao}</p>
         <button className="apagaCliente">🗑️</button>
      </div>))}</>)
}

function lista() {
   return (
      <div className="appLista">
         <header className="appBarLista">
            <ul className="listLista">
               <li className="elemlistLista"><a href="/">Graficos</a></li>
               <li className="elemlistLista" id="elemlistListaDois"><a href="/cliente">Cadastrar Novos Usuarios</a></li>
               <li className="elemlistLista"><a href="/lucros">Lucros</a></li>/
            </ul>
         </header>
         <main className="mainLista">
            <div className="lista">
               {<Router>
                  <Link to="/">Home</Link>
                  <Link to="/outra">Outra</Link>
                  <Route exact path="/" component={Home} />
                  <Route path="/outra" component={Outra} />
               </Router>}
            </div>
         </main>

      </div>
   )
}


export default lista;






