import './App.css';
import { Bar } from 'react-chartjs-2';
import dados from '../src/dados/dadosUsina.json';
console.log(dados)

function App() {
  return (
    <div className="App">
      <header className="appBar">
        <ul>
          <li><a href="#">Gráficos</a></li>
          <li><a href="#">Clientes</a></li>
          <li><a href="#">Lucros</a></li>
        </ul>
      </header>
      <main className="main">


          <form>
            <p>Escolha uma opção para aparecer no gráfico</p>
            <div>
              <input type="checkbox" id="tensao" name="tensao" value="tensao_V"/>
              <label for="tensao">Tensão</label>
              <input type="checkbox" id="temperatura" name="temperatura" value="temperatura_C"/>
              <label for="temperatura">Temperatura</label>
              <input type="checkbox" id="potencia" name="potencia" value="potencia_KW"/>
              <label for="potencia">Potencia</label>
              <input type="checkbox" id="corrent" name="corrent" value="corrent_A"/>
              <label for="corrent">Corrente</label>
            </div>
          </form>
          <div className="grafico">
          <Bar
            {...dados.forEach(element => {
              console.log(element)
            })}
            data={{

              labels: dados.map((elem) => ([elem.value])),
              datasets: [
                {
                  label: 'temperatura por hora ',
                  data: dados.map((elem) => ([elem.tempo_h])),
                  backgroundColor: 'blue',
                },

              ]
            }}
            height={400}
            width={600}
            options={{
              maintainAspectRatio: false,
              scales: {
                yAxes: [
                  {
                    ticks: {
                      beginAtZero: true,
                    }
                  }
                ]
              }
            }}
          />
        </div>

      </main>
    </div>
  );
}

export default App;
