import './App.css';
import { Bar } from 'react-chartjs-2';
import dados from '../src/dados/dadosUsina.json';
console.log(dados)

function App() {
  return (
    <div className="App">
      <header className="appBar">
        <ul>
          <li><a href="#">Gr√°ficos</a></li>
          <li><a href="#"></a></li>
          <li><a href="#">a</a></li>
        </ul>
      </header>
      <main className="main">

        <div className="grafico">

          <Bar
            {...dados.forEach(element => {
              console.log(element.tensao_V)
            })}
            data={{

              labels: dados.map((elem) => ([elem.temperatura_C])),
              datasets: [
                {
                  label: 'temperatura por hora ',
                  data: dados.map((elem) => ([elem.tempo_h])),
                  backgroundColor: 'blue',
                },

              ]
            }}
            height={400}
            width={600}
            options={{
              maintainAspectRatio: false,
              scales: {
                yAxes: [
                  {
                    ticks: {
                      beginAtZero: true,
                    }
                  }
                ]
              }
            }}
          />
        </div>

      </main>
    </div>
  );
}

export default App;
