import dados from '../dados/dadosClientes.json';
import '../design/lista.css';


async function returnDados() {
  const url = "http://localhost:3001/dados";
  fetch(url)
  .then(response => response.json())
  .then(dados => {
   dados.map((items) => (<div className="ficha"><p className="p">Nome do Cliente: </p><p className="p">{items.nomeCliente}</p><p className="p">Identificação do Cliente: </p><p className="p">{items.numeroCliente}</p><p className="p">Participação na Usina: </p><p className="p">{items.usinas[0].percentualDeParticipacao}</p><button className="apagaCliente">🗑️</button></div>))  })
};
function lista() {
   return (
      <div className="appLista">
         <header className="appBarLista">
            <ul className="listLista">
               <li className="elemlistLista"><a href="/">Graficos</a></li>
               <li className="elemlistLista" id="elemlistListaDois"><a href="/cliente">Cadastrar Novos Usuarios</a></li>
               <li className="elemlistLista"><a href="/lucros">Lucros</a></li>/
            </ul>
         </header>
         <main className="mainLista">
            <div className="lista">
               {returnDados()}
            </div>
         </main>
      </div>
   )
}
export default lista;
