import React,{useState} from 'react';
import '../design/App.css';
import { Bar } from 'react-chartjs-2';
import dados from '../dados/dadosUsina.json';


function App() {

  const [valor, setvalor] = useState("");
  const [pos, setpos] = useState("");

  var a = valor;
  /*tempo_h: number;
    tensao_V: number;
    corrente_A: number;
    potencia_kW: number;
    temperatura_C */

  function select() {
    switch (valor) {
      case 'tensao':
        console.log('primeira op')
         setpos(dados.map((elem) => ([elem.tensao_V])))
        break;
      case 'tempo':
        console.log('primeira op')
         setpos(dados.map((elem) => ([elem.tempo_h])))
        break;
      case 'corrente':
        console.log('primeira op')
         setpos(dados.map((elem) => ([elem.corrente_A])))
        break;
      case 'potencia':
        console.log('primeira op')
         setpos(dados.map((elem) => ([elem.potencia_kW])))
        break;
      case 'temperatura':
        console.log('primeira op')
         setpos(dados.map((elem) => ([elem.temperatura_C])))
        break;


      default:
        break;
    }

  }

  return (

    <div className="App">
      <header className="appBar">
        <ul>
          <li><a href="/">Gráficos</a></li>
          <li><a href="/cliente">Clientes</a></li>
          <li><a href="/lucros">Lucros</a></li>
        </ul>
      </header>
      <main className="main">
        <div className="grafico">
          <Bar
            data={{
              labels:pos,
              datasets: [
                {
                  label: valor +'por hora ',
                  data: dados.map((elem) => ([elem.tempo_h])),
                  backgroundColor: '#00a3a2',
                },
              ]
            }}
            height={400}
            width={600}
            options={{
              maintainAspectRatio: false,
              scales: {
                yAxes: [
                  {
                    ticks: {
                      beginAtZero: true,
                    }
                  }
                ]
              }
            }}
          />

        </div>
        <form className="form">
          <p className="p">Escolha uma opção para aparecer no gráfico</p>
          <div>
            <input className="input" type="radio" id="tensao" name="op" value={valor} onChange={e => setvalor('tensao', console.log(valor),select())} />
            <label className="label"for="tensao">Tensão</label>
            <input className="input"type="radio" id="temperatura" name="op" value="temperatura_C" />
            <label className="label"for="temperatura">Temperatura</label>
            <input className="input"type="radio" id="potencia" name="op" value="potencia_KW" />
            <label className="label"for="potencia">Potencia</label>
            <input className="input"type="radio" id="corrent" name="op" value="corrent_A" />
            <label className="label"for="corrent">Corrente</label>
          </div>
        </form>
      </main>
    </div>

  );

}


export default App;
