import dados from '../dados/dadosClientes.json';
import '../design/lista.css';
import React, { UseState } from "react";

/*<div className="lista">
               {dado.map((items) => (console.log(items),<div className="ficha"><p className="p">Nome do Cliente: </p><p className="p">{items.userclientes}</p><p className="p">Identifica√ß√£o do Cliente: </p><p className="p">{items.usinaid}</p><p className="p">Participa√ß√£o na Usina: </p><p className="p">{items.perctparticipacao}</p><button className="apagaCliente">üóëÔ∏è</button></div>))}
            </div> */
var da = [];

async function returnDados() {
   const [usercliente, setFirstName] = UseState([]);
   const url = "http://localhost:3001/dados";
   fetch(url)
      .then(response => response.json())
      .then(dados => {
         setFirstName(dado)
         return dados.map(function (dado) {
     //       console.log(dado)
           
            da.push(dado)
            console.log(usercliente)
            console.log(da)
            console.log(da.length)
         })
      })
   return da
};
const dado = returnDados();

console.log(da.length)
function lista() {
   return (
      <div className="appLista">
         <header className="appBarLista">
            <ul className="listLista">
               <li className="elemlistLista"><a href="/">Graficos</a></li>
               <li className="elemlistLista" id="elemlistListaDois"><a href="/cliente">Cadastrar Novos Usuarios</a></li>
               <li className="elemlistLista"><a href="/lucros">Lucros</a></li>/
            </ul>
         </header>
         <main className="mainLista">
            <div className="lista">
               {
                  da.map((items) => (
                     <div className="ficha">
                        <p className="p">Nome do Cliente: </p>
                        <p className="p">{items.userclientes}</p>
                        <p className="p">Identifica√ß√£o do Cliente: </p>
                        <p className="p">{items.usinaid}</p>
                        <p className="p">Participa√ß√£o na Usina: </p>
                        <p className="p">{items.perctparticipacao}</p>
                        <button className="apagaCliente">üóëÔ∏è</button>
                     </div>))
               }
            </div>
         </main>
      </div>
   )
}
export default lista;
