import React, { useState } from "react";
import '../design/cliente.css';
import Axios from 'axios';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import useSWR from "swr";


function Cliente() {

   const [usercliente, setFirstName] = useState("");
   const [usinaid, setIdUsina] = useState("");
   const [perctParticipacao, setpercParticipacao] = useState("");



   

   const Outra = () => {
      return (
         <>
         <div className="cadastro">
               <h1>Cadastro de Novos Clientes</h1>
               <form className="formulario" >
                  <label className="info">Nome:</label>
                  <input
                     type="text"
                     className="entrada"
                     value={usercliente}
                     onChange={e => setFirstName(e.target.value)}
                  />
                  <label className="info">ID Usina:</label>
                  <input
                     type="text"
                     className="entrada"
                     value={usinaid}
                     onChange={e => setIdUsina(e.target.value)}
                  />
                  <label className="info">Percentual de paticipação: </label>
                  <input
                     type="text"
                     className="entrada"
                     value={perctParticipacao}
                     onChange={e => setpercParticipacao(e.target.value)} />
               </form>
               <button className="botao" onClick={enviarBanco()}>Salvar</button>
            </div>
         </>
      )
   }

   return (
      <div className="App">
         <header>
            <div className="appbar">
               <ul className='ul'>
                  <li><a href="/">Gráfico</a></li>
                  <li><a href="/lista">Lista de Clientes</a></li>
               </ul>
            </div>
         </header>
         <main className="Main">
         {<Router>
                  <Link to="/">Home</Link>
                  <Link to="/outra">Outra</Link>
                  <Route exact path="/" component={Home} />
                  <Route path="/outra" component={Outra} />
               </Router>}
         </main>
      </div>
   )
   function enviarBanco() {
      if (usercliente != "" && usinaid != "" && perctParticipacao != "") {
         Axios.post("http://localhost:3001/register", {
            usercliente: usercliente,
            usinaid: usinaid,
            perctParticipacao: perctParticipacao,

         }).then((response) => {
            console.log('deu erro', response)
         })
      }
   }
}

export default Cliente;
