import React, { useState } from "react";
import Axios from 'axios';



const CadastraCliente = () => {
   const [usercliente, setFirstName] = useState("");
   const [usinaid, setIdUsina] = useState("");
   const [perctParticipacao, setpercParticipacao] = useState("");

   return (
      <div className="App">
         <header>
            <div className="appbar">
               <ul className='ul'>
                  <li><a href="/">Gráfico</a></li>
                  <li><a href="/cliente">Gráfico</a></li>
               </ul>
            </div>
         </header>
         <main className="Main">
            <div className="cadastro">
               <h1>Cadastro de Novos Clientes</h1>
               <form className="formulario" >
                  <label className="info">Nome:</label>
                  <input
                     type="text"
                     className="entrada"
                     value={usercliente}
                     onChange={e => setFirstName(e.target.value)}
                  />
                  <label className="info">ID Usina:</label>
                  <input
                     type="text"
                     className="entrada"
                     value={usinaid}
                     onChange={e => setIdUsina(e.target.value)}
                  />
                  <label className="info">Percentual de paticipação: </label>
                  <input
                     type="text"
                     className="entrada"
                     value={perctParticipacao}
                     onChange={e => setpercParticipacao(e.target.value)} />
               </form>
               <button onClick={function(){enviarBanco()}} className="botao">Salvar</button>
            </div>
         </main>
      </div>


   )

   function enviarBanco() {
      if (usercliente !== "" && usinaid != "" && perctParticipacao != "") {
         Axios.post("http://localhost:3001/register", {
            usercliente: usercliente,
            usinaid: usinaid,
            perctParticipacao: perctParticipacao,

         }).then((response) => {
            console.log(response)
         })
      }
   }
}
export default CadastraCliente;
