import '../design/cliente.css';
import Axios from 'axios';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import useSWR from "swr";


function Cliente() {


   const fetcher = async (url) => {
      const res = await fetch(url);
      const json = await res.json();
      return json;
   }

   const Home = () => {
      const { data, mutate } = useSWR("http://localhost:3001/dados", fetcher)
      if (!data) {
         return <p>Loading...</p>
      }
      return (<>{data.map((items) => (
         <div className="ficha">
            <p className="p">Nome do Cliente: </p>
            <p className="p">{items.userclientes}</p>
            <p className="p">Identificação do Cliente: </p>
            <p className="p">{items.usinaid}</p>
            <p className="p">Participação na Usina: </p>
            <p className="p">{items.perctparticipacao}</p>
            <button data-indice={items.idclientes} onClick={function () { apagaDado(items.idclientes) }} className="apagaCliente">🗑️</button>
         </div>))}</>)
   }

   return (
      <div className="App">
         <header>
            <div className="appbar">
               <ul className='ul'>
                  <li><a href="/">Gráfico</a></li>
                  <li><a href="/register">Registrar</a></li>
               </ul>
            </div>
         </header>
         <main className="Main">
            <div id="aa">
               {<Router>
                  <Route  path="/" component={Home} />
               </Router>}
            </div>
         </main>
      </div>
   )
   function apagaDado(data) {
      console.log(data)
      Axios.post("http://localhost:3001/apaga", { where: data }).then((response) => {
         console.log("deu erro para apagar os dados", response)
      })

   }

}

export default Cliente;
