//import dado from './dado';
import '../design/lista.css';
import React, { UseState,UseEffect } from "react";


function lista() {

   const [dado, setDado] = UseState([]);

   UseEffect(() => {
      fetch("http://localhost:3001/dados").then(({data}) => {
         setDado(data);
      });
     
   }, []);
   console.log(dado)

   return (
      <div className="appLista">
         <header className="appBarLista">
            <ul className="listLista">
               <li className="elemlistLista"><a href="/">Graficos</a></li>
               <li className="elemlistLista" id="elemlistListaDois"><a href="/cliente">Cadastrar Novos Usuarios</a></li>
               <li className="elemlistLista"><a href="/lucros">Lucros</a></li>/
            </ul>
         </header>
         <main className="mainLista">
            <div className="lista">
               {dados.map((items) => (console.log(items),<div className="ficha"><p className="p">Nome do Cliente: </p><p className="p">{items.userclientes}</p><p className="p">Identificação do Cliente: </p><p className="p">{items.usinaid}</p><p className="p">Participação na Usina: </p><p className="p">{items.perctparticipacao}</p><button className="apagaCliente">🗑️</button></div>))}
            </div>
         </main>
      </div>
   )
}
export default lista;
