import List from "./api";

List.google.charts.load('current', {'packages':['corechart']});

// Set a callback to run when the Google Visualization API is loaded.
List.google.charts.setOnLoadCallback(drawChart);


function drawChart() {

   // Create the data table.
   var data = new List.google.visualization.DataTable();
   data.addColumn('string', 'Topping');
   data.addColumn('number', 'Slices');
   data.addRows([
     ['Mushrooms', 3],
     ['Onions', 1],
     ['Olives', 1],
     ['Zucchini', 1],
     ['Pepperoni', 2]
   ]);

   // Set chart options
   var options = {'title':'How Much Pizza I Ate Last Night',
                  'width':400,
                  'height':300};

   // Instantiate and draw our chart, passing in some options.
   var chart = new List.google.visualization.PieChart(document.getElementsByClassName('grafico'));
   chart.draw(data, options);
}
