import React,{useState} from 'react';
import '../design/App.css';
import { Bar } from 'react-chartjs-2';
import dados from '../dados/dadosUsina.json';


function App() {

  const [tensao, setTensao] = useState("");


  return (
    <div className="App">
      <header className="appBar">
        <ul>
          <li><a href="/">Gráficos</a></li>
          <li><a href="/cliente">Clientes</a></li>
          <li><a href="/lucros">Lucros</a></li>
        </ul>
      </header>
      <main className="main">
        <div className="grafico">
          <Bar
            data={{
              labels: dados.map((elem) => ([elem.tensao_V])),
              datasets: [
                {
                  label: 'temperatura por hora ',
                  data: dados.map((elem) => ([elem.tempo_h])),
                  backgroundColor: '#00a3a2',
                },
              ]
            }}
            height={400}
            width={600}
            options={{
              maintainAspectRatio: false,
              scales: {
                yAxes: [
                  {
                    ticks: {
                      beginAtZero: true,
                    }
                  }
                ]
              }
            }}
          />

        </div>
        <form className="form">
          <p className="p">Escolha uma opção para aparecer no gráfico</p>
          <div>
            <input className="input" type="radio" id="tensao" name="op" value={tensao} onChange={e => setTensao(e.target.value, console.log(tensao))} />
            <label className="label"for="tensao">Tensão</label>
            <input className="input"type="radio" id="temperatura" name="op" value="temperatura_C" />
            <label className="label"for="temperatura">Temperatura</label>
            <input className="input"type="radio" id="potencia" name="op" value="potencia_KW" />
            <label className="label"for="potencia">Potencia</label>
            <input className="input"type="radio" id="corrent" name="op" value="corrent_A" checked />
            <label className="label"for="corrent">Corrente</label>
          </div>
        </form>
      </main>
    </div>
  );

}
console.log(tensao)

export default App;
