import React, { useState } from "react";
import Axios from 'axios';
import { Formik, Form, field, ErrorMessage, Field } from "formik";
import * as yup from "yup";



const CadastraCliente = () => {
   const [usercliente, setFirstName] = useState("");
   const [usinaid, setIdUsina] = useState("");
   const [perctParticipacao, setpercParticipacao] = useState("");

   const handleClick = (values) => console.log(values)

   



   return (
      <div className="App">
         <header>
            <div className="appbar">
               <ul className='ul'>
                  <li><a href="/">Gráfico</a></li>
                  <li><a href="/cliente">Gráfico</a></li>
               </ul>
            </div>
         </header>
         <main className="Main">
            <div className="cadastro">
               <h1>Cadastro de Novos Clientes</h1>
               <Formik initialValues={{}} onSubmit={handleClick} validationSchema={validationCadastro}>
                  <Form className="cadastro-form">
                     <div className="cadastro-form-group">
                        <Field name="name" className="form-field" placeholder="Nome" />
                        <ErrorMessage component="span" name="name" className="form-error" />
                     </div>
                     <div className="cadastro-form-group">
                        <Field name="idusina" className="form-field" placeholder="ID-Usina" />
                        <ErrorMessage component="span" name="idusina" className="form-error" />
                     </div>
                     <div className="cadastro-form-group">
                        <Field name="part" className="form-field" placeholder="Participaçâo" />
                        <ErrorMessage component="span" name="part" className="form-error" />
                     </div>
                     <button className="button-form" type="submit">Cadastrar</button>
                  </Form>
               </Formik>
            </div>
         </main>
      </div>


   )

   function enviarBanco() {
      if (usercliente !== "" && usinaid !== "" && perctParticipacao !== "") {
         Axios.post("http://localhost:3001/register", {
            usercliente: usercliente,
            usinaid: usinaid,
            perctParticipacao: perctParticipacao,

         }).then((response) => {
            console.log(response)
         })
      }
   }






}
export default CadastraCliente;



